\begin{code}
select dogs.dog_id, dogs.owner_id, dogs.abandoned_yn, dogs.breed_code, dogs.size_code, dogs.name, dogs.age, dogs.date_of_birth, dogs.gender, dogs.weight, dogs.date_arrived, dogs.date_adopted, dogs.date_departed
from dogs
where dogs.age = (select max(age) from dogs);
\end{code}

