from __future__ import print_function
import sqlite3
from collections import defaultdict


def find_matches(item1, item2, list1, list2, out=None):
    # This function is for checking if two lists share a common element
    if list1 == list2:
        return True
    elif list1 == [] or list2 == []:
        return False
    elif list1[0] == list2[0]:
        return find_matches(item1, list2[1:], list1[1:], list2, out=out)
    elif list1[0] != list2[0]:
        return False
    else:
        return find_matches(item1, list2[1:], list1[1:], list2, out=out)

