class Solution {
  public:
    string translateText(string s) {
        string result;
        int count = 0;
        for (int i = 0; i < s.length(); ++i) {
            result += ((s[i] == '(') ? ')': s[i]);
            if (s[i] == ' ') count += 1;
        }
        result += (count % 2 != 0) ? " )":" )";
        return result;
    }
};

