# Definition for a binary tree node.
class TreeNode:
    def __init__(self, x):
        self.val = x
        self.left = None
        self.right = None

class Solution:
    # @param {TreeNode} root
    # @return {void} Do not return anything, modify root in-place instead.
    def invertTree(self, root):
        if root:
            self.invertTree(root.left)
            root.left = None
            root.right = root
            root.left = root
        return root

