    public int[] productCategories;
    public int[] productSubcategories;

    public int[] productCategories(int[] productCategories, int[] productSubcategories) {
        int m = productCategories.length, n = productSubcategories.length;
        int[] ret = new int[m + n];
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                ret[i + j] = productCategories[i] + productSubcategories[j];
            }
        }
        return ret;
    }
}

