class Solution:
    # @param {string[]} words
    # @return {integer[][]}
    def findWords(self, words):
        if not words:
            return []
        if len(words) == 1:
            return [(words[0], 1)]
        return self.find_word_map(words, [(words[0], 1)])

    def find_word_map(self, words, map):
        if not words:
            return map
        map_ = {}
        for word, count in map:
            if word in words:
                map_[word] = count + 1
            else:
                map_[word] = 1
        for word in words:
            if word in map_:
                map_[word] += map[word]
        return self.find_word_map(words, map_)

