#Given two strings, s1 and s2, determine if s2 is a subsequence of s1.
def subsequence(s1, s2):
	n1 = len(s1)
	n2 = len(s2)
	if n2 > n1:
		return False
	for i in range(n2):
		if s1[i:i+n2] == s2:
			return True
	return False
