CREATE PROCEDURE `get_singer` (IN _id_ INT)
BEGIN WORK;
SET @s = SELECT * FROM Singer WHERE Singer_ID = _id_;
IF @s IS NOT NULL THEN
SELECT Singer.Name FROM Singer WHERE Singer_ID = _id_;
