class Database(object):
    def __init__(self):
        self.conductors = {}
        self.orchestras = {}
        self.performances = {}
        self.shows = {}

    def import_sql(self, filename):
        with open(filename, 'r') as f:
            for line in f:
                if 'SELECT' in line:
                    self.parse_sql(line)
                else:
                    self.parse_dict(line)

    def parse_sql(self, line):
        (sql_parts, params) = line.strip().split(', ')
        if '(' in sql_parts:
            sql = sql_parts
        else:
            sql = 'SELECT * FROM {} WHERE {}'.format(sql_parts, params[0])
            params = params[1:]
        sql = ''.join(sql_parts)

